																																																	<p style="text-indent: 0cm; margin-bottom: 0cm; line-height: 115%;" align="justify"><span style="font-family: arial,helvetica,sans-serif; font-size: 10pt;">$lssql="delete from <strong>nombre de la tabla en la bd </strong> where (<strong>atributo1</strong>='$this-&gt;<strong> atributo1</strong>')";</span></p>
																																																	<p style="text-indent: 0cm; margin-bottom: 0cm; line-height: 115%;" align="justify"><span style="font-family: arial,helvetica,sans-serif; font-size: 10pt;">$this-&gt;fconectar();</span></p>
																																																	<p style="text-indent: 0cm; margin-bottom: 0cm; line-height: 115%;" align="justify"><span style="font-family: arial,helvetica,sans-serif; font-size: 10pt;">$lbhecho=$this-&gt;fejecutar($lssql);</span></p>
																																																	<p style="text-indent: 0cm; margin-bottom: 0cm; line-height: 115%;" align="justify"><span style="font-family: arial,helvetica,sans-serif; font-size: 10pt;">$this-&gt;fdesconectar();</span></p>
																																																	<p style="text-indent: 0cm; margin-bottom: 0cm; line-height: 115%;" align="justify"><span style="font-family: arial,helvetica,sans-serif; font-size: 10pt;">return $lbhecho;}</span></p>
