																																																	<p style=" margin-bottom: 0cm; line-height: 115%;" align="justify"><span style="font-family: arial,helvetica,sans-serif; font-size: 10pt;"> {</span></p>
																																																	<p style=" margin-bottom: 0cm; line-height: 115%;" align="justify"><span style="font-family: arial,helvetica,sans-serif; font-size: 10pt;">$lamatriz=array();</span></p>
																																																	<p style=" margin-bottom: 0cm; line-height: 115%;" align="justify"><span style="font-family: arial,helvetica,sans-serif; font-size: 10pt;">$lii=0;</span></p>
																																																	<p style=" margin-bottom: 0cm; line-height: 115%;" align="justify"><span style="font-family: arial,helvetica,sans-serif; font-size: 10pt;">$lssql="select * from <strong>nombre de la tabla en la bd </strong> order by <strong>atributo1</strong>";</span></p>
																																																	<p style=" margin-bottom: 0cm; line-height: 115%;" align="justify"><span style="font-family: arial,helvetica,sans-serif; font-size: 10pt;">$this-&gt;fconectar();</span></p>
																																																	<p style=" margin-bottom: 0cm; line-height: 115%;" align="justify"><span style="font-family: arial,helvetica,sans-serif; font-size: 10pt;">$lrtb=$this-&gt;ffiltro($lssql);</span></p>
																																																	<p style=" margin-bottom: 0cm; line-height: 115%;" align="justify"><span style="font-family: arial,helvetica,sans-serif; font-size: 10pt;">while($larow=$this-&gt;fproximo($lrtb))</span></p>
																																																	<p style=" margin-bottom: 0cm; line-height: 115%;" align="justify"><span style="font-family: arial,helvetica,sans-serif; font-size: 10pt;">{</span></p>
																																																	<p style=" margin-bottom: 0cm; line-height: 115%;" align="justify"><span style="font-family: arial,helvetica,sans-serif; font-size: 10pt;">$lamatriz[$lii][0]=$larow["<strong>atributo1</strong>"];</span></p>
																																																	<p style=" margin-bottom: 0cm; line-height: 115%;" align="justify"><span style="font-family: arial,helvetica,sans-serif; font-size: 10pt;"> $lamatriz[$lii][1]=$larow["<strong>atributo2</strong>"];</span></p>
